import Header from './Components/Header'
import Login from './Components/Login'
import Register from './Components/Register'
import Cart from './Components/Cart'
import './App.css';
import {useState} from 'react';

function App() {
  const [login, setlogin] = useState(true);
  const [register, showregister] = useState(false);
  const [cart, setcart] = useState(false)

  const showRegister = () => {
    setlogin(false)
  }

  const LoginCheck = (logindetails) => {
    console.log('LoginCheck  *********');
    let details = localStorage.getItem('Details')
    let parseDetails = JSON.parse(details);
    console.log(parseDetails);
    if(parseDetails.email === logindetails.emailId && parseDetails.password === logindetails.password) {
      setcart(true);
    } else {
      setcart(false);
      alert('wrong details');
    }
  }

  const signup = (values) => {
    console.log('signup  *********');
    localStorage.setItem('Details', JSON.stringify(values));
    setlogin(true)
  }

  return (
    <div>
      <Header loggedIn={register} click={showRegister} />
      { login === true && <Login LoginCheck={LoginCheck} /> }
      { login === false && <Register signup={signup} /> }
      { login === false && cart === true && <Cart /> }
    </div>
  );
}

export default App;
