import Header from './Components/Header'
import Login from './Components/Login'
import Register from './Components/Register'
import Cart from './Components/Cart'
import Users from './Components/Users'
import './App.css';
import {useState} from 'react';
import { useHistory } from "react-router-dom";

function App() {
  const [login, setlogin] = useState(true);
  const [register, showregister] = useState(false);
  const [cart, setcart] = useState(false);
  const [count, setcount] = useState(0)
  const history = useHistory();
  const cartCount = () => {
    setcount(count + 1)
  }

  const showRegister = () => {
    setlogin(false);
    showregister(true);
    setcart(false);
  }

  const LoginCheck = (logindetails) => {
    console.log('LoginCheck  *********');
    let details = localStorage.getItem('Details')
    let parseDetails = JSON.parse(details);
    if(parseDetails.email === logindetails.emailId && parseDetails.password === logindetails.password) {
      setlogin(false);
      showregister(false);
      setcart(true);
    } else {
      setcart(false);
      alert('wrong details');
    }
  }

  const signup = (values) => {
    console.log('signup  *********');
    localStorage.setItem('Details', JSON.stringify(values));
    setlogin(true)
  }

  const linkRoute = () => {
    history.push("/link");
  }

  return (
    <div>
      <Header cartcount={count} loggedIn={cart} click={showRegister} linkRoute={linkRoute} />
      { login === true && <Login LoginCheck={LoginCheck} /> }
      { login === false && register === true && <Register signup={signup} /> }
      { login === false && cart === true && <Cart addCount={cartCount} /> }
      { login === false && cart === true && <Users /> }
    </div>
  );
}

export default App;
